SELECT DISTINCT tblReportsStore.txtSchoolID, TblReportsStorePupilArchive.txtForename, TblReportsStorePupilArchive.txtSurname, LOWER(REPLACE(TblReportsStorePupilArchive.txtSurname,'''','')) as txtUnformatSurname, TblReportsStorePupilArchive.txtPreName, TblReportsStorePupilArchive.txtForm, TblReportsStorePupilArchive.intNCYear, TblReportsStorePupilArchive.txtAcademicHouse, TblReportsStorePupilArchive.txtBoardingHouse FROM tblReportsStore INNER JOIN TblReportsStorePupilArchive ON tblReportsStore.txtSchoolID COLLATE DATABASE_DEFAULT = TblReportsStorePupilArchive.txtSchoolID COLLATE DATABASE_DEFAULT AND TblReportsStorePupilArchive.intReportCycle = tblReportsStore.intReportCycle WHERE tblReportsStore.intReportCycle = 71 AND tblReportsStore.intProgress = 1 AND (TblReportsStorePupilArchive.txtForm = 'Year 8A') ORDER BY txtUnformatSurname, txtForename
